    return (
        <div className='Confirmation'>
          <p
            className='Confirmation__title'
          >Для підтвердження запису до лікаря треба ввести ці данні</p>
          <div>
            <p className='Confirmation__text'>Email</p>
            <form
              onSubmit={emailInputHandler}
