                  className='Confirmation__input'
                  value={emailInput}
                  onChange={e => setEmailInput(e.target.value)}
              />
            </form>
          </div>

          <div>
            <p className='Confirmation__text'>Номер телефону</p>
            <form
              onSubmit={phoneInputHandler}
            >
              <input
                  placeholder="+380 (063) 548 99 19"
                  className='Confirmation__input'
                  value={emailInput}
                  onChange={e => setEmailInput(e.target.value)}
              />
            </form>
          </div>

        </div>
    );
};
